import{_ as s,c as a,o as e,e as l}from"./app.e6ae406b.js";const u=JSON.parse('{"title":"在 Android 平台使用 KiviBot","description":"","frontmatter":{},"headers":[{"level":2,"title":"安装 Termux APP","slug":"install-termux","link":"#install-termux","children":[]},{"level":2,"title":"配置 Termux 国内镜像源","slug":"change-source","link":"#change-source","children":[]},{"level":2,"title":"安装 Node.js","slug":"install-nodejs","link":"#install-nodejs","children":[]},{"level":2,"title":"全局安装 KiviBot CLI","slug":"install-kivibot","link":"#install-kivibot","children":[]},{"level":2,"title":"通过 CLI 引导生成配置文件","slug":"generate-config","link":"#generate-config","children":[]},{"level":2,"title":"启动 KiviBot","slug":"start-kivibot","link":"#start-kivibot","children":[]},{"level":2,"title":"后台部署（可选）","slug":"deploy-kivibot","link":"#deploy-kivibot","children":[]}],"relativePath":"guide/start/android.md","lastUpdated":null}'),n={name:"guide/start/android.md"},o=l(`<h1 id="android" tabindex="-1">在 Android 平台使用 KiviBot <a class="header-anchor" href="#android" aria-hidden="true">#</a></h1><h2 id="install-termux" tabindex="-1">安装 Termux APP <a class="header-anchor" href="#install-termux" aria-hidden="true">#</a></h2><p>到 <a href="https://github.com/termux/termux-app/releases" target="_blank" rel="noreferrer">GitHub</a> 下载 Termux 的 APK 安装包, 优先选择 <code>arm64-v8a</code> 版本, 不可用再尝试使用其他版本。</p><blockquote><p>如果 GitHub 打不开或者下载速度太慢，可以到<a href="https://jq.qq.com/?_wv=1027&amp;k=iK97X7NS" target="_blank" rel="noreferrer">交流群</a>的群文件下载。</p></blockquote><h2 id="change-source" tabindex="-1">配置 Termux 国内镜像源 <a class="header-anchor" href="#change-source" aria-hidden="true">#</a></h2><p>可以使用如下命令自动替换官方源为阿里云镜像源，命令来自<a href="https://developer.aliyun.com/mirror/termux" target="_blank" rel="noreferrer">阿里云镜像站</a>。</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#ABB2BF;">sed </span><span style="color:#D19A66;">-i</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;s@^\\(deb.*stable main\\)$@#\\1\\ndeb https://mirrors.aliyun.com/termux/termux-packages-24 stable main@&#39;</span><span style="color:#ABB2BF;"> </span><span style="color:#E06C75;">$PREFIX</span><span style="color:#98C379;">/etc/apt/sources.list</span></span>
<span class="line"><span style="color:#ABB2BF;">sed </span><span style="color:#D19A66;">-i</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;s@^\\(deb.*games stable\\)$@#\\1\\ndeb https://mirrors.aliyun.com/termux/game-packages-24 games stable@&#39;</span><span style="color:#ABB2BF;"> </span><span style="color:#E06C75;">$PREFIX</span><span style="color:#98C379;">/etc/apt/sources.list.d/game.list</span></span>
<span class="line"><span style="color:#ABB2BF;">sed </span><span style="color:#D19A66;">-i</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">&#39;s@^\\(deb.*science stable\\)$@#\\1\\ndeb https://mirrors.aliyun.com/termux/science-packages-24 science stable@&#39;</span><span style="color:#ABB2BF;"> </span><span style="color:#E06C75;">$PREFIX</span><span style="color:#98C379;">/etc/apt/sources.list.d/science.list</span></span>
<span class="line"></span>
<span class="line"><span style="color:#ABB2BF;">pkg </span><span style="color:#98C379;">update</span></span>
<span class="line"></span></code></pre></div><h2 id="install-nodejs" tabindex="-1">安装 Node.js <a class="header-anchor" href="#install-nodejs" aria-hidden="true">#</a></h2><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#ABB2BF;">pkg </span><span style="color:#98C379;">install</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">nodejs</span></span>
<span class="line"></span></code></pre></div><h2 id="install-kivibot" tabindex="-1">全局安装 KiviBot CLI <a class="header-anchor" href="#install-kivibot" aria-hidden="true">#</a></h2><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#ABB2BF;">npm </span><span style="color:#98C379;">i</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">-g</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">kivibot@latest</span></span>
<span class="line"></span></code></pre></div><blockquote><p>当然，你也可以选择不安装到全局，然后使用 <code>npx kivi</code> 来执行 CLI 命令。</p></blockquote><p>如果速度慢, 考虑从国内源安装（仅作备用, 可能不是最新版本）</p><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#ABB2BF;">npm </span><span style="color:#98C379;">i</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">-g</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">kivibot@latest</span><span style="color:#ABB2BF;"> </span><span style="color:#D19A66;">--registry</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">https://registry.npmmirror.com</span></span>
<span class="line"></span></code></pre></div><h2 id="generate-config" tabindex="-1">通过 CLI 引导生成配置文件 <a class="header-anchor" href="#generate-config" aria-hidden="true">#</a></h2><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#7F848E;font-style:italic;"># 创建空目录作为框架目录, 并切换到这个目录</span></span>
<span class="line"><span style="color:#ABB2BF;">mkdir </span><span style="color:#98C379;">~/bot</span><span style="color:#ABB2BF;"> &amp;&amp; </span><span style="color:#56B6C2;">cd</span><span style="color:#ABB2BF;"> </span><span style="color:#98C379;">~/bot</span></span>
<span class="line"><span style="color:#7F848E;font-style:italic;"># 通过 CLI 引导在当前目录下生成配置文件</span></span>
<span class="line"><span style="color:#ABB2BF;">kivi </span><span style="color:#98C379;">init</span></span>
<span class="line"></span></code></pre></div><h2 id="start-kivibot" tabindex="-1">启动 KiviBot <a class="header-anchor" href="#start-kivibot" aria-hidden="true">#</a></h2><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#ABB2BF;">kivi </span><span style="color:#98C379;">start</span></span>
<span class="line"></span></code></pre></div><blockquote><p>如果发现二维码显示异常, 可能是由于终端字体不兼容导致的, 请尝试手动打开 <code>框架目录/data/oicq/[qq]</code> 目录下的二维码图片进行扫码（有效期大约两分钟）。</p></blockquote><h2 id="deploy-kivibot" tabindex="-1">后台部署（可选） <a class="header-anchor" href="#deploy-kivibot" aria-hidden="true">#</a></h2><div class="language-shell"><button title="Copy Code" class="copy"></button><span class="lang">shell</span><pre class="shiki one-dark-pro"><code><span class="line"><span style="color:#ABB2BF;">kivi </span><span style="color:#98C379;">deploy</span></span>
<span class="line"></span></code></pre></div><blockquote><p>CLI 将会使用 pm2 将框架进程部署到后台</p></blockquote>`,22),t=[o];function p(r,i,c,d,h,B){return e(),a("div",null,t)}const b=s(n,[["render",p]]);export{u as __pageData,b as default};
